*{
  box-sizing: border-box;
}
body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 0;
  background-color: #f0f0f0;
}

header {
  background-color: #333;
  color: #fff;
  padding: 10px;
}

nav ul {
  list-style-type: none;
  padding: 0;
  display: flex;
}

nav ul li {
  display: inline;
  margin-right: 20px;
}

nav a {
  text-decoration: none;
  color: #fff;
}

main {
  padding: 20px;
}

.post {
  background-color: #fff;
  padding: 20px;
  margin: 10px;
  border: 1px solid #ddd;
}

.post h2 a {
  text-decoration: none;
  color: #333;
}

.comments {
  background-color: #f0f0f0;
  padding: 10px;
  margin-top: 10px;
}

/* Add more styling as needed */

/* ... (previous CSS code) ... */

.draft-form {
  background-color: #fff;
  padding: 20px;
  margin: 10px;
  border: 1px solid #ddd;
}

label {
  display: block;
  margin-bottom: 5px;
}

input,
textarea {
  padding: 10px 5px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

form{
  display: flex;
  flex-direction: column;
}
button {
  background-color: #333;
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
}

button:hover {
  background-color: #555;
}

/* Search bar styles */
.search-bar {
  margin: 10px;
  display: flex;
  justify-content: space-between;
}

.search-bar input, .search-bar select {
  width: 200px;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

/* Login/Signup Modal styles */
.login-modal {
  display: block;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  z-index: 9999;
}

.login-content {
  background-color: #fff;
  margin: 15% auto;
  padding: 20px;
  width: 30%;
  border: 1px solid #ddd;
  position: relative;
}

#logoutBtn {
  display: none;
  cursor: pointer;
}

.login-form {
    display: flex;
    flex-direction: column;
}

.toggle-comments{
  margin-top: 10px;
}
.my-draft-buttons{
  display: flex;
  gap: 15px;
}

.delete-draft {
  display: flex;
  justify-content: center;
  align-items: center;
  align-self: center;
  margin: 0;
  align-self: center;
  flex-direction: column;
  margin-top: 10px;
}
